import React from 'react';
import { Button, FormGroup, FormControl } from "react-bootstrap";
import API from '../../utils/API';

export class Signup extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            email : "",
            nom: "",
            prenom: "",
            password: "",

        }
        this.handleChange.bind(this);
        this.send.bind(this);
    }
    send = event => {
        if(this.state.email.length === 0){
            return;
        }
        if(this.state.password.length === 0 || this.state.password){
            return;
        }
        var _send = {
            email: this.state.email,
            password: this.state.password,
            nom: this.state.nom,
            prenom:this.state.prenom

        }
        API.signup(_send).then(function(data){
            localStorage.setItem('token', data.data.token);
            window.location = "/dashboard"
        },function(error){
            console.log(error);
            return;
        })
    }    
    handleChange = event => {
        this.setState({
            [event.target.id]: event.target.value
        });
    }
    render() {
        return(
            <div className="Login">
                <FormGroup controlId="nom" bsSize="large">
                <label>Nom</label>
                <FormControl autoFocus type="text" value={this.state.nom} onChange={this.handleChange}/>
                </FormGroup>
                <FormGroup controlId="prenom" bsSize="large">
                <label>Prenom</label>
                <FormControl value={this.state.prenom} onChange={this.handleChange} type="text"/>
                </FormGroup>
                <FormGroup controlId="email" bsSize="large">
                <label>Email</label>
                <FormControl autoFocus type="email" value={this.state.email} onChange={this.handleChange}/>
                </FormGroup>
                <FormGroup controlId="password" bsSize="large">
                <label>Password</label>
                <FormControl value={this.state.password} onChange={this.handleChange} type="password"/>
                </FormGroup>

                <Button
                onClick={this.send}
                type="submit"
                >
                Inscription
                </Button>
            </div>
        )
    }
}
