import React from 'react';
import { Button, FormGroup, FormControl } from "react-bootstrap";
import API from '../../utils/API';

export class Login extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            nom:'',
            prenom:'',
            email : "",
            password: ""
        }
        this.handleChange.bind(this);
        this.send.bind(this);
    }
    send = event => {
        if(this.state.email.length === 0){
            return;
        }
        if(this.state.password.length === 0){
            return;
        }
        API.login(this.state.email, this.state.password,this.state.nom,this.state.prenom).then(function(data){
            localStorage.setItem('token', data.data.token);
            console.log(data.data.nom)
            window.location = "/dashboard/" + data.data.nom
        },function(error){
            console.log(error);
            return;
        })
    }    
    handleChange = event => {
        this.setState({
            [event.target.id]: event.target.value
        });
    }
    render() {
        return(
            <div className="Login">
                 <FormGroup controlId="nom" bsSize="large">
                <label>Nom</label>
                <FormControl autoFocus type="text" value={this.state.nom} onChange={this.handleChange}/>
                </FormGroup>
                <FormGroup controlId="prenom" bsSize="large">
                <label>Prenom</label>
                <FormControl value={this.state.prenom} onChange={this.handleChange} type="text"/>
                </FormGroup>
                <FormGroup controlId="email" bsSize="large">
                <label>Email</label>
                <FormControl autoFocus type="email" value={this.state.email} onChange={this.handleChange}/>
                </FormGroup>
                <FormGroup controlId="password" bsSize="large">
                <label>Password</label>
                <FormControl value={this.state.password} onChange={this.handleChange} type="password"/>
                </FormGroup>
                <Button
                onClick={this.send}
                type="submit">
                Connexion
                </Button>
            </div>
        )
    }
}
